INSERT INTO `article` VALUES ('55', '29', '其他', '1', '2016-05', 'base64编码输出图片', '什么是Base64编码Base64编码要求把3个8位字节（3*8=24）转化为4个6位的字节（4*6=24），之后在6位的前面补两个0，形成8位一个字节的形式。 如果剩下的字符不足3个字节，则用0填充', '1464678442', '1503059721', '0', '64', 'Windows 7', 'Chrome 60.0', '0', '重庆市电信', '', '22', 'base64编码');
