INSERT INTO `words` VALUES ('10', '人生就像是一满杯水。每个人都想向你来要水。选择一：你可以把你的水给每个人分一点，倒进他们的水杯里——你的精力在流失，你的时间在流逝。选择二：集中精力做你喜欢的事情，让你的水溢出，流出的水分给你身边的人，需要的人。', '0', '1503047783');
